mapper.select:
SELECT
*
FROM
report
WHERE
r_from_date = #{fromdate}
AND r_to_date = #{todate}
AND status_mod = #{status}

SELECT
*
FROM
report


		 r_from_date >= #{fromdate}


       AND r_to_date <= #{todate}

AND status_l_sub = #{status}
AND dept_id=#{deptId}

order by author_name, r_from_date

SELECT
*
FROM
report


		r_from_date >= #{fromdate}


		AND r_to_date <= #{todate}

	AND status_l_sub = #{status}

order by dept_name,author_name, r_from_date
